import { Observable } from 'rxjs';
export interface IFormFields {
    type: 'select' | 'select-search' | 'input' | 'checkbox' | 'submit' | 'header';
    selectContent?: IFormFieldSelect;
    selectSearchContent?: IFormFieldSelectSearch;
    inputContent?: IFormFieldInput;
    checkboxContent?: IFormFieldCheckbox;
    submitContent?: IFormFieldSubmit;
    headerContent?: IFormFieldHeader;
}
export interface IFormFieldHeader {
    headerTitle: string;
    headerFormat: 'h1' | 'h2' | 'h3';
}
export interface IFormFieldSelect {
    selectLabel: string;
    selectOptionItems: IFormFieldSelectOptionItems[];
}
export interface IFormFieldSelectOptionItems {
    selectOptionLabel: string;
    selectOptionValue: string;
}
export interface IFormFieldSelectSearch {
    name: string;
    selectIndex: number;
    items: Observable<any>;
    label?: string;
    placeholder: string;
    multiple: boolean;
    bindLabel: string;
    bindValue: string;
}
export interface IFormFieldInput {
    inputLabel: string;
    inputType: 'button' | 'checkbox' | 'color' | 'date' | 'datetime-local' | 'email' | 'file' | 'hidden' | 'image' | 'month' | 'number' | 'password' | 'radio' | 'range' | 'reset' | 'search' | 'string' | 'submit' | 'tel' | 'text' | 'time' | 'url' | 'week';
    inputClass?: string;
    inputName: string;
    inputId?: string;
    inputRefVariable?: string;
    inputNgModelRequired: boolean;
    inputNgModelMinLength?: number | null;
    inputNgModelMaxLength?: number | null;
    inputValidationPattern?: string | RegExp;
    inputDisabled?: boolean;
    inputAlerting?: IFormFieldAlerting;
    inputValueDefault?: string;
}
export interface IFormFieldAlerting {
    alertErrorTextRequired: string;
    alertErrorTextLength?: string;
    alertErrorTextPattern?: string;
}
export interface IFormFieldCheckbox {
    checkboxId?: string;
    checkboxName: string;
    checkboxLabel: string;
    checkboxCheckedState: boolean;
}
export interface IFormFieldSubmit {
    submitLabel: string;
    submitIconFaClass?: string;
    submitDisabled: boolean;
}
